import { monitoredEntitiesClient } from '@dynatrace-sdk/client-classic-environment-v2';

export default async function() {
  // Check if that tile should be activated based on selected indicators
  if($Indicators[0] === "All" ||
     $Indicators.includes("fullStackCoverage") ||
     $Indicators.includes("candidates") ||
     $Indicators.includes("hostGroups") ||
     $Indicators.includes("hostGroupCoverage")) {
    let scope;
    if($Scope[0] === "Tenant") {
      scope = 'Tenant';
    } else {
      let mzList = "";
      for(let i=0; i<$Scope.length; i++) {
        mzList += i === 0 ? `"${$Scope[i]}"` : `,"${$Scope[i]}"`;
      }
      scope = mzList;
    }
    // Define entity selector based on the selected scope
    let entitySelector;
    if(scope === "Tenant") {
      entitySelector = 'type("HOST"),isMonitoringCandidate(false)';
    } else {
      entitySelector = `type("HOST"),isMonitoringCandidate(false),mzName(${scope})`;
    }
    const config = { from: `now-1d`, entitySelector: entitySelector};
    // Get all monitored hosts
    const objects = await monitoredEntitiesClient.getEntities(config);
    // Return the count
    return objects.totalCount;
  } else {
    return "N/A";
  }
}

