import { monitoredEntitiesClient } from '@dynatrace-sdk/client-classic-environment-v2';

export default async function() {
  // Check if that tile should be activated based on selected indicators and scope
  if($Indicators[0] === "All" || $Indicators.includes("hostGroups") || $Indicators.includes("hostGroupCoverage")) {
    let scope;
    if($Scope[0] === "Tenant") {
      scope = 'Tenant';
    } else {
      let mzList = "";
      for(let i=0; i<$Scope.length; i++) {
        mzList += i === 0 ? `"${$Scope[i]}"` : `,"${$Scope[i]}"`;
      }
      scope = mzList;
    }
    // Define entity selector based on the selected scope
    let entitySelector;
    if(scope === "Tenant") {
      entitySelector = 'type("HOST_GROUP")';
    } else {
      entitySelector = `type("HOST_GROUP"),toRelationships.isInstanceOf(type("HOST"),mzName(${scope}))`;
    }
    const config = {
      from: `now-5m`,
      entitySelector: entitySelector,
      fields: '+toRelationships'
    };
    // Get all host groups and associated hosts
    const objects = await monitoredEntitiesClient.getEntities(config);
    // Format results for table visualization
    const hostGroups = objects.entities.map(el => {
      const filteredHostGroupEntry = {};
      filteredHostGroupEntry["Host group"] = el.displayName;
      filteredHostGroupEntry.Hosts = el.toRelationships.isInstanceOf.length;
      filteredHostGroupEntry.entityId = el.entityId;
      return filteredHostGroupEntry;
    });

    // Return the list
    return hostGroups.length > 0 ? hostGroups : { "Host group": "‚ö†Ô∏è No host group defined"};
  } else {
    return { "Host group": "N/A"};
  }
}

