import { monitoredEntitiesClient } from '@dynatrace-sdk/client-classic-environment-v2';

export default async function() {
  // Check if that tile should be activated based on selected indicators and scope
  if($Indicators[0] === "All" || $Indicators.includes("uniqueServices") || $Indicators.includes("customServices")) {
    let scope;
    if($Scope[0] === "Tenant") {
      scope = 'Tenant';
    } else {
      let mzList = "";
      for(let i=0; i<$Scope.length; i++) {
        mzList += i === 0 ? `"${$Scope[i]}"` : `,"${$Scope[i]}"`;
      }
      scope = mzList;
    }
    // Define entity selector based on the selected scope
    let entitySelector;
    if(scope === "Tenant") {
      entitySelector = 'type("SERVICE")';
    } else {
      entitySelector = `type("SERVICE"),mzName(${scope})`;
    }
    let config = { from: "now-1d", entitySelector: entitySelector, sort: "name", pageSize: 12000 };
    // Get all services
    let objects = await monitoredEntitiesClient.getEntities(config);
    let services = objects.entities.map(el => {
  return {
    "Service": el.displayName,
    "Duplicate": `‚úÖ NO`,
    entityId: el.entityId
  };
});
while(objects.nextPageKey) {
  config = { nextPageKey: objects.nextPageKey };
  objects = await monitoredEntitiesClient.getEntities(config);
  const otherServices = objects.entities.map(el => {
    return {
      "Service": el.displayName,
      "Duplicate": `‚úÖ NO`,
      entityId: el.entityId
    };
  });
  services = [...services, ...otherServices];
}
// Helper function to filter duplicates
const lookup = services.reduce((a, e) => {
  a[e["Service"]] = ++a[e["Service"]] || 0;
  return a;
}, {});
let duplicateServices = [];
let uniqueServices = [];
// Segment unique and duplicate services
for(let service of services) {
  if(lookup[service["Service"]]) {
    service["Duplicate"] = `‚ö†Ô∏è YES`;
    duplicateServices.push(service);
  } else {
    uniqueServices.push(service);
  }
}
// List duplicate services first, unique ones second
const allServices = [...duplicateServices, ...uniqueServices];
// List services
return allServices.length > 0 ? allServices.slice(0, 500) : { "Service": "‚ö†Ô∏è No service detected"};
  } else {
    return { "Service": "N/A"};
  }
}

