import { settingsObjectsClient, monitoredEntitiesClient, metricsClient  } from '@dynatrace-sdk/client-classic-environment-v2';
import { rumUserSessionsClient } from "@dynatrace-sdk/client-classic-environment-v1";

export default async function() {
  // Check if that tile should be activated based on selected indicators and scope
  if($Indicators[0] === "All" || $Indicators.includes("webApps")) {
    // Get list of app IDs and names
    let config = { schemaIds: 'builtin:rum.web.name', fields: 'value, scope', pageSize: 500 };
    let objects = await settingsObjectsClient.getSettingsObjects(config);
    const webApps = [];
    // Prepare template object for each monitored Web app
    for(let object of objects.items) {
      webApps.push({
        id: object.scope,
        'Web app name': object.value.applicationName === "My web application" ? "‚ö†Ô∏è My web application ‚ö†Ô∏è" : object.value.applicationName,
        '7-day traffic': object.value.applicationName === 'My web application' ? "‚úÖ NO" : "‚ö†Ô∏è NO",
        RUM: "‚úÖ ON",
        'Synthetic tests': "‚ö†Ô∏è 0",
        'User tags': "üí° NO",
        'Key User Actions': 0,
        Properties: 0,
        Conversions: "üí° NO",
        'Session Replay': "üí° OFF",
        'App detection rules': "",
        'JS agent version': "LATEST_STABLE"
      })
    }
    // Collect non-default settings for RUM and Session Replay enablement
    config.schemaIds = 'builtin:rum.web.enablement';
    objects = await settingsObjectsClient.getSettingsObjects(config);
    for(let object of objects.items) {
      let webAppIndex = webApps.findIndex(el => el.id === object.scope);
      if(webAppIndex > -1) {
        webApps[webAppIndex].RUM = object.value.rum.enabled ? "‚úÖ ON" : "‚ö†Ô∏è OFF";
        webApps[webAppIndex]['Session Replay'] = object.value.sessionReplay.enabled ? "‚úÖ ON" : "üí° OFF";
      }
    }
    // Count associated synthetic tests for each Web app
    for(let i=0; i < webApps.length; i++) {
      let config = { from: "now-1d", entitySelector: `type("SYNTHETIC_TEST"),fromRelationships.monitors(type("APPLICATION"),entityId("${webApps[i].id}"))`};
      const objects = await monitoredEntitiesClient.getEntities(config);
      let result;
      switch(true) {
        case objects.totalCount == 0:
          result = `‚ö†Ô∏è ${objects.totalCount}`;
          break;
        case objects.totalCount < 2:
          result = `üí° ${objects.totalCount}`;
          break;
        default:
          result = `‚úÖ ${objects.totalCount}`;
      }
      webApps[i]['Synthetic tests'] = result;
    }
    // Collect user tag rules
    let userTaggedApps = await rumUserSessionsClient.getUsqlResultAsTable({
      query: "SELECT application FROM useraction WHERE (usersession.userId IS NOT NULL) GROUP BY application LIMIT 500",
      startTimestamp: (Date.now() - 7*24*60*60*1000),
      pageSize: 500
    });
    userTaggedApps = userTaggedApps.values;
    for(let userTaggedApp of userTaggedApps) {
      let webAppIndex = webApps.findIndex(el => el['Web app name'] === userTaggedApp[0]);
      if(webAppIndex > -1) {
        webApps[webAppIndex]['User tags'] = `‚úÖ YES`;
      }
    }
    // Collect app detection rules
    config.schemaIds = 'builtin:rum.web.app-detection';
    objects = await settingsObjectsClient.getSettingsObjects(config);
    for(let object of objects.items) {
      let webAppIndex = webApps.findIndex(el => el.id === object.value.applicationId);
      if(webAppIndex > -1) {
        webApps[webAppIndex]['App detection rules'] = webApps[webAppIndex]['App detection rules'] ? `${webApps[webAppIndex]['App detection rules'] + object.value.matcher} ${object.value.pattern}; ` : `${object.value.matcher} ${object.value.pattern}; `;
      }
    }
    for(let i=0; i < webApps.length; i++) {
      webApps[i]['App detection rules'] = webApps[i]['App detection rules'] ? webApps[i]['App detection rules'] : "‚ö†Ô∏è NO RULES";
    }
    // Collect Javascript agent version settings
    config.schemaIds = 'builtin:rum.web.rum-javascript-updates';
    objects = await settingsObjectsClient.getSettingsObjects(config);
    let envJsUpdatesIndex = objects.items.findIndex(el => el.scope === "environment");
    if(envJsUpdatesIndex > -1) {
      for(let i=0; i < webApps.length; i++) {
        webApps[i]['JS agent version'] = objects.items[envJsUpdatesIndex].value.JavascriptVersion;
      }
    }
    for(let object of objects.items) {
      let webAppIndex = webApps.findIndex(el => el.id === object.scope);
      if(webAppIndex > -1) {
        webApps[webAppIndex]['JS agent version'] = object.value.JavascriptVersion;
      }
    }
    // Determine which apps received traffic in the last 7 days
    config = { from: "now-7d", entitySelector: 'type("APPLICATION")'};
    objects = await monitoredEntitiesClient.getEntities(config);
    for(let object of objects.entities) {
      let webAppIndex = webApps.findIndex(el => el.id === object.entityId);
      if(webAppIndex > -1) {
        webApps[webAppIndex]['7-day traffic'] = webApps[webAppIndex]['Web app name'] === '‚ö†Ô∏è My web application ‚ö†Ô∏è' ? "‚ö†Ô∏è YES" : "‚úÖ YES";
      }
    }
    // For each app, determine which key user actions received traffic in the last 7 days
    config = { from: "now-7d", fields: '+fromRelationships', entitySelector: 'type(application_method)'};
    objects = await monitoredEntitiesClient.getEntities(config);
    for(let object of objects.entities) {
      if(object.fromRelationships.isApplicationMethodOf) {
        let webAppIndex = webApps.findIndex(el => el.id === object.fromRelationships.isApplicationMethodOf[0].id);
        if(webAppIndex > -1) {
          webApps[webAppIndex]['Key User Actions']++;
        }
      }
    }
    // For each app, determine how many user action and session properties got captured in the last 7 days
    config = { from: "now-7d", acceptType: "application/json; charset=utf-8", metricSelector: 'builtin:billing.apps.web.userActionPropertiesByApplication:splitBy("dt.entity.application"):sort(value(auto,descending)):limit(100):fold(value)'};
    objects = await metricsClient.query(config);
    if(objects.result.data) {
      for(let object of objects.result) {
        let webAppIndex = webApps.findIndex(el => el.id === object.data[0].dimensionMap['dt.entity.application']);
        if(webAppIndex > -1) {
          webApps[webAppIndex].Properties = object.data[0].values[0];
        }
      }
    }
    // For each app, determine if there's any conversion rate captured in the last 7 days
    config = { from: "now-7d", acceptType: "application/json; charset=utf-8", metricSelector: 'builtin:apps.web.conversionRate:splitBy("dt.entity.application"):sort(value(auto,descending)):limit(100):fold(value)'};
    objects = {
      result: []
    };
    try {
     objects = await metricsClient.query(config);
    } catch(e) {}
    if(objects.result[0] && objects.result[0].data[0]) {
      for(let object of objects.result) {
        let webAppIndex = webApps.findIndex(el => el.id === object.data[0].dimensionMap['dt.entity.application']);
        if(webAppIndex > -1) {
          webApps[webAppIndex].Conversions = "‚úÖ YES";
        }
      }
    }

    // If the review is scope to some management zones, filter out apps that are not part of the review scope
    let scope;
    if($Scope[0] === "Tenant") {
      scope = 'Tenant';
    } else {
      let mzList = "";
      for(let i=0; i<$Scope.length; i++) {
        mzList += i === 0 ? `"${$Scope[i]}"` : `,"${$Scope[i]}"`;
      }
      scope = mzList;
    }
    const filteredWebApps = [];
    if(scope !== "Tenant") {
      let entitySelector = `type("APPLICATION"),mzName(${scope})`;
      config = { from: `now-10000d`, entitySelector: entitySelector};
      objects = await monitoredEntitiesClient.getEntities(config);
      for(let object of objects.entities) {
        filteredWebApps.push(webApps.find(el => el.id === object.entityId));
      }
    }


    // Rename the app ID to entityId from the final output, for correct intent fuctioning
    for(let i=0; i < webApps.length; i++) {
      webApps[i].entityId = webApps[i].id;
      delete webApps[i].id;
    }
    // Return list
    if(scope === "Tenant") {
      return webApps.length > 0 ? webApps : { "Web app name": "‚ö†Ô∏è No web app detected"};
    } else {
      return filteredWebApps.length > 0 ? filteredWebApps : { "Web app name": "‚ö†Ô∏è No web app detected"};
    }
  } else {
    return { "Web app name": "N/A"};
  }
}

