        const problemSelector = `managementZones(${scope})`;
        config = { from: "now-7d", problemSelector: problemSelector, pageSize: 500};
      }
      // Get all problems open in the past 7 days
      let objects = await problemsClient.getProblems(config);
      let problems = objects.problems;
      while(objects.nextPageKey) {
        config = { nextPageKey: objects.nextPageKey };
