          }
        ]
      };
      // For each problem, increment the count of problems per severity
      for(let problem of problems) {
        let severityLevelIndex = severityLevelStats.records.findIndex(el => el.severityLevel === problem.severityLevel);
        if(severityLevelIndex > -1) {
