export default async function() {
  // Check if that tile should be activated based on selected indicators and scope
  if($Indicators[0] === "All" || $Indicators.includes("activeGateVersions")) {
    if($Scope[0] === "Tenant") {
      // Determine the latest OneAgent version supported by Dynatrace.
      let objects = await clusterVersionClient.getVersion();
      const latestSupportedVersion = objects.version.split('.')[1];
      const oldestSupportedVersion = objects.version.split('.')[1] - 20;
      objects = await activeGatesClient.getAllActiveGates();
      const activeGates = objects.activeGates.map(el => {
        const filteredEntry = {};
        switch(true) {
        case parseInt(el.version.substring(2,5)) < oldestSupportedVersion:
          filteredEntry.Version = `‚ö†Ô∏è ${el.version}`;
          break;
        case parseInt(el.version.substring(2,5)) < (latestSupportedVersion - 6):
          filteredEntry.Version = `üí° ${el.version}`;
          break;
        default:
          filteredEntry.Version = `‚úÖ ${el.version}`;
        }
        filteredEntry.ActiveGate = el.hostname;
        return filteredEntry;
      });

      return activeGates.sort(function (a, b) {
        if (a.Version.substr(4, 3) > b.Version.substr(4, 3)) {
          return 1;
        }
        if (a.Version.substr(4, 3) < b.Version.substr(4, 3)) {
          return -1;
        }
        return 0;
      });
    } else {
      return {
        "ActiveGate": `N/A`
      };
    }
  } else {
    return {
      "ActiveGate": `N/A`
    };
