import { settingsObjectsClient, monitoredEntitiesClient, monitoredEntitiesCustomTagsClient } from '@dynatrace-sdk/client-classic-environment-v2';

async function getUntaggedEntities(entityType, tags, $Scope) {
  // Retrieve the scope
  let scope;
  if($Scope[0] === "Tenant") {
    scope = 'Tenant';
  } else {
    let mzList = "";
    for(let i=0; i<$Scope.length; i++) {
      mzList += i === 0 ? `"${$Scope[i]}"` : `,"${$Scope[i]}"`;
    }
    scope = mzList;
  }
  // Define entity selector based on the selected scope
  let entitySelector;
  if(scope === "Tenant") {
    entitySelector = `type("${entityType}"),not(tag("${tags.join('","')}"))`;
  } else {
    entitySelector = `type("${entityType}"),not(tag("${tags.join('","')}")),mzName(${scope})`;
  }
  const config = { from: "now-7d", entitySelector: entitySelector, pageSize: 500};
  let objects = [];
  try {
    // Get all untagged entities
    objects = await monitoredEntitiesClient.getEntities(config);
    objects = objects.entities.map(el => {
    return {
      "Untagged entity": `üí° ${el.displayName}`,
      entityId: el.entityId
    };
  });
  } catch(e) {}
  return objects;
}

async function getAllUntaggedEntities(entityTypes, autoTags, $Scope) {
  let allUntaggedEntities = [];
  for(let entityType of entityTypes) {
    let customTags = await monitoredEntitiesCustomTagsClient.getTags({ entitySelector: `type("${entityType}")` });
    customTags = customTags.tags.map(el => el.key);
    const allTags = [...autoTags, ...customTags];
    const untaggedEntities = await getUntaggedEntities(entityType, allTags, $Scope);
    allUntaggedEntities = [...allUntaggedEntities, ...untaggedEntities];
  }
  return allUntaggedEntities;
}

export default async function() {
  // Check if that tile should be activated based on selected indicators and scope
  if($Indicators[0] === "All" || $Indicators.includes("autoTags")) {
    if($Scope[0] === "Tenant") {
      let config = { schemaIds: 'builtin:tags.auto-tagging'};
      // Get all auto tags
      let objects = await settingsObjectsClient.getSettingsObjects(config);
      const autoTags = objects.items.map(el => el.value.name);
      let entityTypes = ['SYNTHETIC_TEST', 'APPLICATION', 'MOBILE_APPLICATION', 'CUSTOM_APPLICATION', 'SERVICE', 'PROCESS_GROUP', 'HOST'];
      const allUntaggedEntities = await getAllUntaggedEntities(entityTypes, autoTags, $Scope);
      // Return list of untagged entities
      return allUntaggedEntities.length > 0 ? allUntaggedEntities : { "Untagged entity": "‚úÖ No untagged entity detected"};
    } else {
      return "N/A";
    }
  } else {
    return "N/A";
  }
}

