  if($Indicators[0] === "All" || $Indicators.includes("uniqueProcessGroups") || $Indicators.includes("declarativeProcessGroupings")) {
    let scope;
    if($Scope[0] === "Tenant") {
      scope = 'Tenant';
    } else {
      let mzList = "";
      for(let i=0; i<$Scope.length; i++) {
        mzList += i === 0 ? `"${$Scope[i]}"` : `,"${$Scope[i]}"`;
      }
      scope = mzList;
    }
    // Define entity selector based on the selected scope
    let entitySelector;
    if(scope === "Tenant") {
      entitySelector = 'type("PROCESS_GROUP")';
    } else {
      entitySelector = `type("PROCESS_GROUP"),mzName(${scope})`;
    }
    let config = { from: "now-5m", entitySelector: entitySelector, sort: "name", pageSize: 12000 };
    // Get all process groups
    let objects = await monitoredEntitiesClient.getEntities(config);
    // Format table entries
    let processGroups = objects.entities.map(el => {
      return {
        "Process group": el.displayName,
        "Duplicate": `‚úÖ NO`,
        entityId: el.entityId
      };
    });
    // Loop through pagination
    while(objects.nextPageKey) {
      config = { nextPageKey: objects.nextPageKey };
      objects = await monitoredEntitiesClient.getEntities(config);
      const otherProcessGroups = objects.entities.map(el => {
        return {
          "Process group": el.displayName,
          "Duplicate": `‚úÖ NO`,
          entityId: el.entityId
        };
      });
      processGroups = [...processGroups, ...otherProcessGroups];
    }
    // Helper function to filter duplicates
    const lookup = processGroups.reduce((a, e) => {
      a[e["Process group"]] = ++a[e["Process group"]] || 0;
      return a;
    }, {});
    let duplicateProcessGroups = [];
    let uniqueProcessGroups = [];
    for(let processGroup of processGroups) {
      if(lookup[processGroup["Process group"]]) {
        processGroup["Duplicate"] = `‚ö†Ô∏è YES`;
        duplicateProcessGroups.push(processGroup);
      } else {
        uniqueProcessGroups.push(processGroup);
      }
    }
    // List duplicate process groups first, unique ones second
    const allProcessGroups = [...duplicateProcessGroups, ...uniqueProcessGroups];
    // Return list
    return allProcessGroups.length > 0 ? allProcessGroups.slice(0, 500) : { "Process group": "‚ö†Ô∏è No process group detected"};
  } else {
    return { "Process group": "N/A"};
  }
}

