  // Compute local score depending on the number of enabled rules
  const score = enabledRules > 10 ? 1 : (enabledRules > 0 ? 0.5 : 0);
  return score;
}

async function bizEventsBucketAssignmentRules($Scope) {
  let objects;
  const config = { schemaIds: 'builtin:bizevents-processing-buckets.rule'};
  try {
    // Get code-level vulnerability detection settings
    objects = await settingsObjectsClient.getSettingsObjects(config);
  } catch(e) {}
  // Filter the enabled metric events
  const enabledRules = objects.items.filter(el => el.value.enabled).length;
  // Compute local score depending on the number of enabled rules
  const score = enabledRules > 20 ? 1 : (enabledRules > 0 ? 0.5 : 0);
  return score;
}

async function limits($Scope) {
  const limits = [];
  // Get all available setting schemas
  const objects = await settingsSchemasClient.getAvailableSchemaDefinitions();
  const settings = objects.items;
  for(let setting of settings) {
    let config = { schemaId: setting.schemaId};
    const schemaData = await settingsSchemasClient.getSchemaDefinition(config);
    if (schemaData.multiObject) {
      const limit = schemaData.maxObjects;
      config = { schemaIds: setting.schemaId};
      const schemaObjects = await settingsObjectsClient.getSettingsObjects(config);
      const count = schemaObjects.totalCount;
      limits.push({
        limitConsumption: parseInt((count/limit)*100),
        schemaId: setting.schemaId,
        name: setting.displayName,
        objectCount: count,
        limit: limit
      });
    }
  }
  // Compute local score based on number of alert, warning and good thresholds
  const score = limits.findIndex(el => el.limitConsumption >= 90) > -1 ? 0 : (limits.findIndex(el => el.limitConsumption >= 75) > -1 ? 0.5 : 1);
  return score;
}

export default async function() {
  let totalWeightedScore = 0;
  // Determine what indicators should be evaluated based on the selected DTR scope
  let scopedIndicators;
  if($Scope[0] === "Tenant") {
    scopedIndicators = tenantIndicators;
  } else {
    scopedIndicators = managementZoneIndicators;
  }
  // Determine what indicators should be evaluated based on the selected indicators
  let selectedIndicators = [];
  if($Indicators[0] === "All") {
    selectedIndicators = scopedIndicators;
  } else {
    for(let scopedIndicator of scopedIndicators) {
      if($Indicators.includes(scopedIndicator.name)) {
        selectedIndicators.push(scopedIndicator); 
      }
    }
  }
  // Evaluate indicators and increment totalWeightedScore by their weighted local score
  for(let selectedIndicator of selectedIndicators) {
    totalWeightedScore += await getWeightedScore(selectedIndicator, $Scope);
  }
  // Compute what's the maximum weighted score that can be expected based on the selected indicators
  const maxWeightedTotalScore = getMaxWeightedTotalScore(selectedIndicators);
  // Divide totalWeightedScore by maxWeightedTotalScore to get the totalScorePercent
  const totalScorePercent = Math.floor((totalWeightedScore / maxWeightedTotalScore) * 100);
  // Return the emojied totalScorePercent
  let result;
  switch(true) {
    case totalScorePercent >= 95:
      result = `‚≠ê ${totalScorePercent}%`;
      break;
    case totalScorePercent >= 80:
      result = `‚úÖ ${totalScorePercent}%`;
      break;
    case totalScorePercent >= 50:
      result = `üí° ${totalScorePercent}%`;
      break;
    default:
      result = `‚ö†Ô∏è ${totalScorePercent}%`;
  }
  return result;
}

