async function oneAgentVersions($Scope) {
  // Determine the latest OneAgent version supported by Dynatrace.
      let objects = await clusterVersionClient.getVersion();
      const latestSupportedVersion = objects.version.split('.')[1];
      const nonRecentOneAgentVersion = latestSupportedVersion - 4;
      const oldestSupportedVersion = latestSupportedVersion - 20;
      // List hosts that are running non-recent (> 3 months old) OneAgent versions
      const config = {
        agentVersionIs: "LOWER",
        agentVersionNumber: `1.${nonRecentOneAgentVersion}.0`,
        includeDetails: false
      };
      objects = await oneAgentOnAHostClient.getHostsWithSpecificAgents(config);
      // Remove hosts with no OneAgent version detected
      let nonRecentOneAgents = objects.hosts.filter((e) => {
        if(e.hostInfo.agentVersion && e.hostInfo.agentVersion.minor) {
          return true;
        } else {
          return false;
        }
      });
      // Count non-recent and unsupported running OneAgents
      let oldOneAgents = 0;
      let unsupportedOneAgents = 0;
      for(let nonRecentOneAgent of nonRecentOneAgents) {
        if(nonRecentOneAgent.hostInfo.agentVersion.minor < oldestSupportedVersion) {
          unsupportedOneAgents++;
        } else {
          oldOneAgents++;
        }
      }
      // Multiply by the sampling factor to get more realistic stats
      oldOneAgents = oldOneAgents * 100 / objects.percentageOfEnvironmentSearched;
      unsupportedOneAgents = unsupportedOneAgents * 100 / objects.percentageOfEnvironmentSearched;
      const score = (unsupportedOneAgents > 0) ? 0 : ((oldOneAgents > 0) ? 0.5 : 1);return score;
      return score;
}

async function activeGateVersions($Scope) {
  const oldestSupportedVersion = Math.round((Date.now()/1000 - 1363125600)/(60*60*24*7*2*2))*2 - 1 - 18;
  const latestVersion = Math.round((Date.now()/1000 - 1363125600)/(60*60*24*7*2*2))*2 - 1 + 2;
  const objects = await activeGatesClient.getAllActiveGates();
  const names = objects.activeGates.map(el => {
    const filteredEntry = {};
    switch(true) {
    case el.version.substring(2,5) < oldestSupportedVersion:
      filteredEntry.Version = `‚ö†Ô∏è ${el.version}`;
      break;
    case el.version.substring(2,5) < (latestVersion - 6):
      filteredEntry.Version = `üí° ${el.version}`;
      break;
    default:
      filteredEntry.Version = `‚úÖ ${el.version}`;
    }
    filteredEntry.ActiveGate = el.hostname;
    return filteredEntry;
  });
  const score = names.findIndex(el => el.Version.substring(4,7) < (latestVersion - 6)) === -1 ? 1 : (names.findIndex(el => el.Version.substring(4,7) < oldestSupportedVersion) === -1 ? 0.5 : 0);
  return score;
}
