async function limits($Scope) {
  const limits = [];
  // Get all available setting schemas
  const objects = await settingsSchemasClient.getAvailableSchemaDefinitions();
  const settings = objects.items;
  for(let setting of settings) {
    let config = { schemaId: setting.schemaId};
    const schemaData = await settingsSchemasClient.getSchemaDefinition(config);
    if (schemaData.multiObject) {
      const limit = schemaData.maxObjects;
      config = { schemaIds: setting.schemaId};
      const schemaObjects = await settingsObjectsClient.getSettingsObjects(config);
      const count = schemaObjects.totalCount;
      limits.push({
        limitConsumption: parseInt((count/limit)*100),
        schemaId: setting.schemaId,
        name: setting.displayName,
        objectCount: count,
        limit: limit
      });
    }
  }
  // Compute local score based on number of alert, warning and good thresholds
  const score = limits.findIndex(el => el.limitConsumption >= 90) > -1 ? 0 : (limits.findIndex(el => el.limitConsumption >= 75) > -1 ? 0.5 : 1);
  return score;
}

export default async function() {
  const evaluations = [];
  // Determine what indicators should be evaluated based on the selected DTR scope
  let scopedIndicators;
  if($Scope[0] === "Tenant") {
    scopedIndicators = tenantIndicators;
  } else {
    scopedIndicators = managementZoneIndicators;
  }
  // Determine what indicators should be evaluated based on the selected indicators
  let selectedIndicators = [];
  if($Indicators[0] === "All") {
    selectedIndicators = scopedIndicators;
  } else {
    for(let scopedIndicator of scopedIndicators) {
      if($Indicators.includes(scopedIndicator.name)) {
        selectedIndicators.push(scopedIndicator); 
      }
    }
  }
  // Evaluate indicators
  for(let selectedIndicator of selectedIndicators) {
    const evaluation = await evaluate(selectedIndicator, $Scope);
    evaluations.push(evaluation);
  }
  // Filter out healthy indicators
  const filteredEvaluations = evaluations.filter(e => e.score !== 1);
  // Segment indicators per importance
  const importantIndicatorEvaluations = filteredEvaluations.filter(e => e.weight === 1);
  const bestPracticeIndicatorEvaluations = filteredEvaluations.filter(e => e.weight === 2);
  const fundamentalIndicatorEvaluations = filteredEvaluations.filter(e => e.weight === 3);
  // Sort each population per normalized score
  const sortedImportantIndicatorEvaluations = sortArrayOfObjects(importantIndicatorEvaluations, "score");
  const sortedBestPracticeIndicatorEvaluations = sortArrayOfObjects(bestPracticeIndicatorEvaluations, "score");
  const sortedFundamentalIndicatorEvaluations = sortArrayOfObjects(fundamentalIndicatorEvaluations, "score");
  // Concatenate groups of evaluations by order of importance
  const finalEvaluations = sortedFundamentalIndicatorEvaluations;
  finalEvaluations.push(...sortedBestPracticeIndicatorEvaluations);
  finalEvaluations.push(...sortedImportantIndicatorEvaluations);
  console.log(finalEvaluations);
  // Extract the recommendation from each evaluation
  const prioritizedRecommendations = finalEvaluations.map(e => {
    return {
      "Prioritized next steps üëá": e.recommendation,
      "Importance": e.weight === 3 ? "Fundamentals" : (e.weight === 2 ? "Best practice" : "Recommended")
    };
  });
  // Return prioritized recommendations
  return prioritizedRecommendations.length > 0 ? prioritizedRecommendations : { "Prioritized next steps üëá": "‚úÖ Well done! All recommendations have been implemented üëè"};
}

