  });
  // Sort OneAgents by their version (ascending)
  nonRecentOneAgents = nonRecentOneAgents.sort(function (a, b) {
    if (a.hostInfo.agentVersion.minor > b.hostInfo.agentVersion.minor) {
      return 1;
    }
    if (a.hostInfo.agentVersion.minor < b.hostInfo.agentVersion.minor) {
      return -1;
    }
    return 0;
  });
  // Format final output
  nonRecentOneAgents = nonRecentOneAgents.map((e) => {
      const oneAgentVersion = '1.' + e.hostInfo.agentVersion.minor;
      const result = {
        "OneAgent version": (e.hostInfo.agentVersion.minor < oldestSupportedVersion ? `‚ö†Ô∏è ${oneAgentVersion}` : `üí° ${oneAgentVersion}`),
        Host: e.hostInfo.displayName,
        entityId: e.hostInfo.entityId
      }
      return result;
  });
  // List hosts that are running recent OneAgent versions
  config = {
    agentVersionIs: "GREATER_EQUAL",
    agentVersionNumber: `1.${nonRecentOneAgentVersion}.0`,
    includeDetails: false
  };
  objects = await oneAgentOnAHostClient.getHostsWithSpecificAgents(config);
  // Remove hosts with no OneAgent version detected
  let recentOneAgents = objects.hosts.filter((e) => {
    if(e.hostInfo.agentVersion && e.hostInfo.agentVersion.minor) {
      return true;
    } else {
      return false;
    }
  });
  // Sort OneAgents by their version (ascending)
  recentOneAgents = recentOneAgents.sort(function (a, b) {
    if (a.hostInfo.agentVersion.minor > b.hostInfo.agentVersion.minor) {
      return 1;
    }
    if (a.hostInfo.agentVersion.minor < b.hostInfo.agentVersion.minor) {
      return -1;
    }
    return 0;
  });
  // Format final output
  recentOneAgents = recentOneAgents.map((e) => {
      const oneAgentVersion = '1.' + e.hostInfo.agentVersion.minor;
      const result = {
        "OneAgent version": `‚úÖ ${oneAgentVersion}`,
        Host: e.hostInfo.displayName,
        entityId: e.hostInfo.entityId
      }
      return result;
  });
  // Concatenate up to 500 non-recent OneAgents with up to 500 recent OneAgents
  const allOneAgents = [...nonRecentOneAgents, ...recentOneAgents];
  if(allOneAgents.length > 0) {
    return allOneAgents;
  } else {
    return { "No OneAgent found": "N/A"};
  }  
}

