import { settingsSchemasClient } from "@dynatrace-sdk/client-classic-environment-v2";
import { settingsObjectsClient } from '@dynatrace-sdk/client-classic-environment-v2';

export default async function() {
  // Check if that tile should be activated based on selected indicators and scope
  if($Indicators[0] === "All" || $Indicators.includes("limits")) {
    if($Scope[0] === "Tenant") {
      const limits = [];
      // Get all available setting schemas
      const objects = await settingsSchemasClient.getAvailableSchemaDefinitions();
      const settings = objects.items;
      for(let setting of settings) {
        let config = { schemaId: setting.schemaId};
        const schemaData = await settingsSchemasClient.getSchemaDefinition(config);
        if (schemaData.multiObject) {
          const limit = schemaData.maxObjects;
          config = { schemaIds: setting.schemaId};
          const schemaObjects = await settingsObjectsClient.getSettingsObjects(config);
          const count = schemaObjects.totalCount;
          limits.push({
            limitConsumption: parseInt((count/limit)*100),
            schemaId: setting.schemaId,
            name: setting.displayName,
            objectCount: count,
            limit: limit
          });
        }
      }
      let sortedLimits = limits.sort(function (a, b) {
          if (a.limitConsumption < b.limitConsumption) {
            return 1;
          }
          if (a.limitConsumption > b.limitConsumption) {
            return -1;
          }
          return 0;
        });
      sortedLimits = sortedLimits.map(e => {
        if(e.limitConsumption >= 90) {
          e.limitConsumption = `‚ö†Ô∏è ${e.limitConsumption} %`;
        } else if(e.limitConsumption >= 75) {
          e.limitConsumption = `üí° ${e.limitConsumption} %`;
        } else {
          e.limitConsumption = `‚úÖ ${e.limitConsumption} %`;
        }
        return e;
      })
      return sortedLimits.length > 0 ? sortedLimits : { "Limits": "‚ö†Ô∏è No setting detected for limits analysis"};
        // objects = await settingsObjectsClient.getSettingsObjects(config);
    } else {
      return { "Limits": "N/A"};
    }
  } else {
    return { "Limits": "N/A"};
  }
}

