import { settingsObjectsClient } from "@dynatrace-sdk/client-classic-environment-v2";

export default async function () {
  let data =
    await settingsObjectsClient.getSettingsObjects({
      schemaIds: "builtin:management-zones",
      scopes: "environment",
      pageSize:500
    });

  let arr = []; 

  let l = data["items"]
  for(let i = 0; i < l.length; i++) {
    let obj = l[i];
    console.log(obj.value.name);
    arr.push(obj["value"]["name"]);
  }

  while (data.hasOwnProperty('nextPageKey')) {
    data = await settingsObjectsClient.getSettingsObjects({
            nextPageKey: data["nextPageKey"]
    });
    let d = data["items"]
    for(let i = 0; i < d.length; i++) {
      let obj = d[i];
      console.log(obj.value.name);
      arr.push(obj["value"]["name"]);
    }  
  }

  return ["Any", ...arr.sort()];
}

import { queryExecutionClient } from "@dynatrace-sdk/client-query";

export default async function() {
  const objects = await queryExecutionClient.queryExecute({
    body: {
      query: 'fetch dt.entity.host, from: -15m | filter isNotNull(osVersion) | fieldsAdd tags | expand tags | filter isNotNull(tags) | summarize by:{tags}, count() | fields tags',
      requestTimeoutMilliseconds: 10000,
      maxResultRecords: 100000
    },
  });
  return ['Any', ...objects.result.records.map(e => e.tags)];
}

